import React, { useState } from 'react';
import styled from "styled-components";

const Heading = styled.div`
    display: block;
    font-weight: bold;
`

const withCollapse = (title, WrappedComponent, showAll) => {
  function WithCollapse(props) {

    const [showThisInstance, setshowThisInstance] = useState(showAll);

    const setShowHide = display => {
      console.log(display)
      setshowThisInstance(display)
    }

    return (
      <div>
        <Heading>
        {title} {showThisInstance ? <Hide setShowHide={setShowHide}/> : <Show setShowHide={setShowHide}/>}
        </Heading>
        {showThisInstance && <WrappedComponent {...props} />}
      </div>)
    }    
  return WithCollapse;
};


function Hide(props) {
  return <span onClick={() => props.setShowHide(false)} aria-label="Hide Button" role="image">⬆️</span>
}

function Show(props) {
  return <span onClick={() => props.setShowHide(true)}>⬇️</span>
}

export default withCollapse;
