import React from 'react';
import styled from "styled-components";

const Heading = styled.div`
    display: block;
    font-weight: bold;
`

const withCollapse = (title, WrappedComponent) => {
  class WithCollapse extends React.Component {
    constructor(props) {
      super(props);
      this.state = { 
        showResults: true,
        emoji: '⬆️'
       };
      this.handleClick = this.handleClick.bind(this);
    }

    handleClick() {

      this.setState(state => ({
        showResults: !state.showResults,
        emoji: state.showResults ? '⬇️' : '⬆️'
      }));
    }

    render() {
      return (
      <div>
        <Heading>
        {title} <span onClick={this.handleClick} role="img"> {this.state.emoji}</span>
        </Heading>
        {this.state.showResults && <WrappedComponent {...this.props} />}
      </div>)
    }
  }

  return WithCollapse;
};

export default withCollapse;
