import React, { useState, useCallback } from 'react';
import styled from "styled-components";

const Heading = styled.div`
    display: block;
    font-weight: bold;
`

const withCollapse = (title, WrappedComponent, showAll) => {
  function WithCollapse(props) {

    const [showThisInstance, setshowThisInstance] = useState(false);

    const shouldDisplay = showAll || showThisInstance;
    const onHide = useCallback(async () => {
        setshowThisInstance(false)
    }, [])

    const onShow = useCallback(async () => {
      setshowThisInstance(true)
  }, [])

    const HideButton = () => <span onClick={onHide} role="img">Show ⬇️</span>;
    const ShowButton = () => <span onClick={onShow} role="img"> Hide ⬆️</span>;

    return (
      <div>
        <Heading>
        {title} {shouldDisplay ? <HideButton /> : <ShowButton /> }
        </Heading>
        {shouldDisplay && <WrappedComponent {...props} />}
      </div>)
    }    
  return WithCollapse;
};




export default withCollapse;
