import React, { useState, useCallback } from 'react';
import styled from "styled-components";

const Heading = styled.div`
    display: block;
    font-weight: bold;
`

const withCollapse = (title, WrappedComponent, showAll) => {
  function WithCollapse(props) {

    const [showThisInstance, setshowThisInstance] = useState(false);
    const [emoji, setEmoji] = useState('⬆️');

    const shouldDisplay = showAll || showThisInstance;


    const dispense = candies => {
      setshowThisInstance(!candies)
      setEmoji(candies ? '⬇️' : '⬆️')
    }

    const onClick = useCallback(async () => {
        setshowThisInstance(!shouldDisplay)
        setEmoji(shouldDisplay ? '⬇️' : '⬆️')
    }, [shouldDisplay])

    return (
      <div>
        <Heading>
        {title} {!showAll && <span onClick={dispense}> {emoji}</span>}
        </Heading>
        {shouldDisplay && <WrappedComponent {...props} />}
      </div>)
    }    
  return WithCollapse;
};

export default withCollapse;
