import React, { useState, useCallback } from 'react';
import styled from "styled-components";

const Heading = styled.div`
    display: block;
    font-weight: bold;
`

const withCollapse = (title, WrappedComponent, showAll) => {
  function WithCollapse(props) {

    const [showThisInstance, setshowThisInstance] = useState(false);

    const shouldDisplay = showAll || showThisInstance;
    const onClick = useCallback(async (display) => {
        setshowThisInstance(display)
    }, [])
    const HideButton = () => <span onClick={onClick(false)}>Show '⬇️'</span>;
    const ShowButton = () => <span onClick={onClick(true)}> Hide '⬆️'</span>;

    return (
      <div>
        <Heading>
        {title} {shouldDisplay ? <HideButton /> : <ShowButton /> }
        </Heading>
        {shouldDisplay && <WrappedComponent {...props} />}
      </div>)
    }    
  return WithCollapse;
};




export default withCollapse;
