import React, { useState, useEffect } from 'react';
import styled from "styled-components";

const Heading = styled.div`
    display: block;
    font-weight: bold;
`

const withCollapse = (title, WrappedComponent, showAll) => {
  function WithCollapse(props) {

    const [showThisInstance, setshowThisInstance] = useState(false);

    useEffect(() => {
      const shouldDisplay = showAll || showThisInstance;
      setshowThisInstance(shouldDisplay)
    }, [showThisInstance]);

    const setShowHide = display => {
      console.log(display)
      setshowThisInstance(display)
    }

    console.log('shouldDisplay', showThisInstance)
    return (
      <div>
        <Heading>
        {title} {showThisInstance ? <Hide setShowHide={setShowHide}/> : <Show setShowHide={setShowHide}/>}
        </Heading>
        {showThisInstance && <WrappedComponent {...props} />}
      </div>)
    }    
  return WithCollapse;
};


function Hide(props) {
  return <span onClick={() => props.setShowHide(false)}> Show</span>
}

function Show(props) {
  return <span onClick={() => props.setShowHide(true)}> Hide </span>
}

export default withCollapse;
