import React from 'react';
import styled, { css } from "styled-components";

const Conditions = styled.div`
    display: grid;
    grid-template-columns: repeat(1, [col-start] 1fr);
    grid-gap: 2rem;
    @media (min-width:961px) {
        grid-template-columns: repeat(4, [col-start] 1fr);
        padding: 2rem;
    }
`

const withCollapse = (title, WrappedComponent) => {
  class WithCollapse extends React.Component {
    constructor(props) {
      super(props);
      this.state = { 
        showResults: true,
        emoji: '⬇️'
       };
      this.handleClick = this.handleClick.bind(this);
    }

    handleClick() {

      this.setState(state => ({
        showResults: !state.showResults,
        emoji: state.showResults ? '⬇️' : '⬆️'
      }));
    }

    render() {
      return (
      <div>
        {title} <div onClick={this.handleClick}> {this.state.emoji}</div>
        {this.state.showResults && <WrappedComponent {...this.props} />}
      </div>)
    }
  }

  return WithCollapse;
};

export default withCollapse;
