import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import AlbumListing from '../src/ compoents/AlbumListing'
import getAlbums from './state/selectors/getAlbums';
import withCollapse from '../src/ compoents/withCollapse';
import { useSelector, useDispatch } from "react-redux";
import fetchSongs from './state/actions/fetchSongs'

// Use container pattern to decouple conerns from grid
const AlbumListingContainer = connect(getAlbums)(AlbumListing);

// Use Higher order component to reduce duplicate of collapse
const AlbumListingWithCollapse = withCollapse('Albums', AlbumListing);

function App() {
  useFetching(fetchSongs);
  const [albums, hasAlbums] = useState(true);
  console.log('1', albums);
  console.log(hasAlbums);
  return (
    <AlbumListing albumns={albums} hasAlbums={hasAlbums}></AlbumListing>
  );
}


const useFetching = (action) => {
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(action());
  }, [])
}

export default App;
