import React, { useState, useCallback } from 'react';
import styled from "styled-components";

const Heading = styled.div`
    display: block;
    font-weight: bold;
`

const withCollapse = (title, WrappedComponent, showAll) => {
  function WithCollapse(props) {

    const [showThisInstance, setshowThisInstance] = useState(false);
    const shouldDisplay = showAll || showThisInstance;


    const dispense = candies => {
      console.log(candies)
      setshowThisInstance(!candies)

    }

    const onClick = useCallback(async () => {
        setshowThisInstance(!shouldDisplay)
    }, [shouldDisplay])

    return (
      <div>
        <Heading>
        {title} {shouldDisplay ? <Show onClick={dispense}/> : <Hide onClick={dispense}/>}
        </Heading>
        {shouldDisplay && <WrappedComponent {...props} />}
      </div>)
    }    
  return WithCollapse;
};


function Hide(props) {
  return <span onClick={() => props.onClick(false)}> Show</span>
}

function Show(props) {
  return <span onClick={() => props.onClick(true)}> Hide </span>
}

export default withCollapse;
