import React from 'react';
import fetchSongs from '../state/actions/fetchSongs'
import Albumn from './Album'
import withCollapse from './withCollapse'

class AlbumListing extends React.Component {
  componentDidMount() {
    this.props.dispatch(fetchSongs());
  }

  render() {
    const { hasAlbums, albums } = this.props;
    if (!hasAlbums) {
      return <div>Loading</div>;
    }

    const items = albums.map((album, index) => {
      const title = `${album.band} = ${album.album}`;
      const WithCollapse = withCollapse(title, Albumn);
      return <WithCollapse key={index} band={album.band} songs={album.songs} album={album.album}/>
    });

    return (
      <div>
        {items}
      </div>
    );
  }
}

export default AlbumListing;
