export const calcauleScore = (dice: Array<number>) => {

    if (!dice)
        return;

    const playsScore = dice[0] + dice[1];
    const monstersScore = dice[3] + dice[4];

    console.log('currentPlayersHealth', playsScore)
    console.log('currentMonsterHealth', monstersScore)

    let result = {
        playsScore,
        monstersScore
    }

    if (playsScore === monstersScore) {
        console.log('draw')
    } else if (monstersScore > playsScore) {
        console.log('monstor wins')   
        const diff = diceDifference(playsScore, monstersScore)
        console.log(diff)
        result.playersDamage = playerHealth - diff;
    } else {
        console.log('player wins')
        const diff = diceDifference(monstersScore, playsScore)
        console.log(diff)
        result.monstersDamage = monstersScore - diff
    }

    return result;
}

export default calcauleScore;
