function rollDice() {
    var min = Math.ceil(1);
    var max = Math.floor(6);
    var roll = Math.floor(Math.random() * (max - min + 1)) + min;
    return roll
}

export const diceDifference = function (a, b) { return Math.abs(a - b); }

export default rollDice;
