import React, { useState } from 'react';
import Dice from './components/Game/Dice';
import Player from './components/Game/Player';
import Monster from './components/Game/Monster';
import Dashboard from './components/Game/Dashboard';
import styled from "styled-components"
import { GameStateProvider } from './context/GameStateContext';
import rollDice from './utils/rollDice';

// used stylised component for simple rendering
const Container = styled.div`
  display: grid;
  grid-template-columns: repeat(4, 1fr);
`

function App() {

    const [ playerHealth, setPlayerHealth ] = useState(100);
    const [ monsterHealth, setMonsterHealth ] = useState(100);
    const [ dice, setDice ] = useState([]);

    function handleGameClick() {
        const diceOne = rollDice();
        const diceTwo = rollDice();

        const diceThree : number = rollDice();
        const diceFour = rollDice();

        const playersRoll = diceOne + diceTwo;
        const monstersRoll = diceThree + diceFour;

        if (playersRoll === monstersRoll) {
            console.log('draw')
        } else  if (monstersRoll >= playersRoll) {
            console.log('player wins')
            const diff = monstersRoll - playersRoll
            setPlayerHealth(playerHealth - diff)
        } else {
            const diff = playersRoll - monstersRoll;
            setPlayerHealth(playerHealth - diff)
            console.log('monstor wins')   
        }

        if (playerHealth <= 0) {
            console.log('play loses')
        } else if (monsterHealth  <= 0) {
            console.log('monster loses')
        }
      }

    return (
        <GameStateProvider value={{playerHealth, setPlayerHealth, monsterHealth, setMonsterHealth, dice, setDice}}>
            <Container>
                <Player></Player>
                <div>
                    <Dice roll={diceOne}></Dice>
                    <Dice roll={diceTwo}></Dice>
                </div>
                <div>
                    <Dice roll={diceThree}></Dice>
                    <Dice roll={diceFour}></Dice>
                </div>
                <Monster></Monster>
            </Container>
            <Dashboard attackClick={handleGameClick}></Dashboard>
        </GameStateProvider>
)}

export default App;
