import React, { FunctionComponent, useContext, Fragment } from 'react';
import styled from "styled-components"
import GameStateContext from '../../context/GameStateContext';
import GameStatus from '../../types/GameStatus'

const DashBoardPanel = styled.div`
  text-align: center;
`

const Button = styled.button`
  color: #fff;
  text-transform: uppercase;
  text-decoration: none;
  background: #ed3330;
  padding: 20px;
  border-radius: 5px;
  display: inline-block;
  border: none;
  transition: all 0.4s ease 0s;
`

const Message = styled.div`
  text-align: center;
  color: black:
`

const WinningMessage = styled.div`
  color: green:
`

const LosingMessage = styled.div`
  color: green:
`

interface DashboardProps {
  attackClick: Function
}

const Dashboard: FunctionComponent<DashboardProps> = ({attackClick}) => {
    const { gameStatus } = useContext(GameStateContext)
    console.log('gs', gameStatus)
    return (
    <DashBoardPanel>
      <Message>
        {gameStatus === GameStatus.Fighting ||
         !gameStatus && <Fragment><a href="https://www.jondjones.com/frontend/react/">JonDJones</a> </Fragment>}
        {gameStatus === GameStatus.PlayerWin && <WinningMessage>Winner</WinningMessage>}
        {gameStatus === GameStatus.PlayerWin && <LosingMessage>Loser</LosingMessage>}
      </Message>
      <Button onClick={() => attackClick()}>Attack!</Button>
    </DashBoardPanel>
)}

export default Dashboard;
