import React, { useState } from 'react';
import Dice from './components/Game/Dice';
import Player from './components/Game/Player';
import Monster from './components/Game/Monster';
import Dashboard from './components/Game/Dashboard';
import styled from "styled-components"
import { GameStateProvider } from './context/GameStateContext';
import rollDice, { diceDifference } from './utils/dice';
import getLoser from './utils/getLoser';
import GameStatus from './types/GameStatus'

// used stylised component for simple rendering
const Container = styled.div`
  display: grid;
  grid-template-columns: repeat(4, 1fr);
`

const Score = styled.div`
    text-align: center;
    font-size: 4em;
    font-weight: bold;
`

const MonsterArea = styled.div`
`

function App() {

    const [ playerHealth, setPlayerHealth ] = useState(100);
    const [ monsterHealth, setMonsterHealth ] = useState(100);
    const [ gameStatus, setGameStatus ] = useState(GameStatus.Fighting);
    const [ dice, setDice ] = useState();

    const [ currentPlayersHealth, setCurrentPlayersHealth ] = useState(0);
    const [ currentMonsterHealth, setCurrentMonsterHealth ] = useState(0);

    console.log('ph', playerHealth)

    function handleGameClick() {
        const diceOne : number = rollDice();
        const diceTwo : number = rollDice();

        const diceThree : number = rollDice();
        const diceFour : number = rollDice();

        const playsScore = diceOne + diceTwo;
        setCurrentPlayersHealth(playsScore);

        const monstersScore = diceThree + diceFour;
        setCurrentMonsterHealth(monstersScore);

        console.log('currentPlayersHealth', playsScore)
        console.log('currentMonsterHealth', monstersScore)

        if (playsScore === monstersScore) {
            console.log('draw')
        } else if (monstersScore > playsScore) {
            console.log('monstor wins')   

            const diff = diceDifference(playsScore, monstersScore)
            console.log(diff)
            const newHealth = playerHealth - diff
            setMonsterHealth(newHealth)
        } else {
            console.log('player wins')
            const diff = diceDifference(monstersScore, playsScore)
            console.log(diff)
            const newHealth = playerHealth - diff
            setPlayerHealth(newHealth)

        }

        const status = getLoser(playerHealth, monsterHealth);
        setGameStatus(status)

        const dice : Array<number> = [diceOne, diceTwo, diceThree, diceFour];
        setDice(dice);
      }

    return (
        <GameStateProvider value={{playerHealth, setPlayerHealth, monsterHealth, setMonsterHealth, dice, setDice}}>
            <Container>
                <Player></Player>
                <MonsterArea>
                    <Dice roll={dice?.[0]}></Dice>
                    <Dice roll={dice?.[1]}></Dice>
                    {currentPlayersHealth !== 0 && 
                        <Score>
                            {currentPlayersHealth}
                        </Score>}
                </MonsterArea>
                <MonsterArea>
                    <Dice roll={dice?.[2]}></Dice>
                    <Dice roll={dice?.[3]}></Dice>
                    <Score>
                        {currentMonsterHealth}
                    </Score>
                </MonsterArea>
                <Monster></Monster>
            </Container>
            <Dashboard attackClick={handleGameClick}></Dashboard>
        </GameStateProvider>
)}

export default App;
