`

interface DashboardProps {
  attackClick: Function,
  gameStatus: GameStatus
}

const Dashboard: FunctionComponent<DashboardProps> = ({attackClick, gameStatus}) => {
    console.log('gs', gameStatus)
    return (
    <DashBoardPanel>
      <Button onClick={() => attackClick()}>Attack!</Button>
      <Message>
        {(gameStatus === GameStatus.Fighting || gameStatus.MonsterWin) && <Fragment><a href="https://www.jondjones.com/frontend/react/">JonDJones</a> </Fragment>}
        {gameStatus === GameStatus.PlayerWin  && <WinningMessage>Winner</WinningMessage>}
        {gameStatus === GameStatus.MonsterWin && <LosingMessage>Loser</LosingMessage>}
      </Message>

    </DashBoardPanel>
)}

export default Dashboard;
